import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import axios from "../api/api";
import PaymentModal from "./PaymentModal";
import PaymentHistory from "./PaymentHistory";

const StudentPage = () => {
  const [student, setStudent] = useState({});
  const [bills, setBills] = useState([]);
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedBill, setSelectedBill] = useState(null);
  const [creditBalance, setCreditBalance] = useState(0);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    setStudent({
      firstName: user.firstName,
      email: user.identifier,
      id: user.id,
    });

    const fetchStudentData = async () => {
      try {
        const billsResponse = await axios.get(`/api/v1/students/bills`, {
          headers: { studentId: user.id },
        });
        const paymentsResponse = await axios.get(`/api/v1/payments`, {
          headers: { studentId: user.id },
        });

        setBills(billsResponse.data);
        setPayments(paymentsResponse.data);
        if (billsResponse.data.length !== 0)
          setCreditBalance(billsResponse.data[0].creditBalance);
      } catch (error) {
        console.error("Error fetching student data:", error);
        alert("Failed to load student data.", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudentData();
  }, []);

  const handleOpenPaymentModal = (bill) => {
    setSelectedBill(bill);
  };

  const handleClosePaymentModal = async () => {
    setSelectedBill(null);
    try {
      const billsResponse = await axios.get(`/api/v1/students/bills`, {
        headers: { studentId: student.id },
      });
      const paymentsResponse = await axios.get(`/api/v1/payments`, {
        headers: { studentId: student.id },
      });

      setBills(billsResponse.data);
      setPayments(paymentsResponse.data);
    } catch (error) {
      console.error("Error refreshing bills:", error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar name={student.firstName} email={student.email} />

      <div className="container mx-auto px-6 py-8">
        {/* Credit Balance Section */}
        <div className="text-center mb-10">
          <h1 className="text-2xl font-bold text-gray-800">
            Welcome, {student.firstName}
          </h1>
          <p className="text-lg text-gray-600 mt-2">
            Your Credit Balance: <span className="font-semibold">₹{creditBalance}</span>
          </p>
        </div>

        {/* Bills Section */}
        <div className="mb-10">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Pending Bills</h2>
          {loading ? (
            <p className="text-gray-600">Loading bills...</p>
          ) : bills.length === 0 ? (
            <p className="text-gray-600">You have no pending bills.</p>
          ) : (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {bills.map((bill) => (
                <div
                  key={bill.billId}
                  className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow duration-300"
                >
                  <h3 className="text-lg font-semibold text-gray-800">
                    {bill.description}
                  </h3>
                  <p className="text-gray-600 mt-2">Amount: ₹{bill.amount}</p>
                  <p className="text-gray-600">Due Date: {bill.dueDate}</p>
                  <p className="text-gray-600">Deadline: {bill.deadline}</p>
                  <button
                    onClick={() => handleOpenPaymentModal(bill)}
                    className="mt-4 w-full bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-md"
                  >
                    Pay Now
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Payment History Section */}
        <div className="mt-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Payment History</h2>
          <PaymentHistory payments={payments} />
        </div>
      </div>

      {/* Payment Modal */}
      {selectedBill && (
        <PaymentModal
          isOpen={!!selectedBill}
          onClose={handleClosePaymentModal}
          billId={selectedBill.billId}
          totalDue={selectedBill.currentDue}
          creditBalance={creditBalance}
          studentId={student.id}
        />
      )}
    </div>
  );
};

export default StudentPage;
